import logo from './logo.svg';
import './App.css';

// import useState to work with variables and events
import { useState } from 'react';
// import axios so it can be used
import axios from 'axios';

const getStarWarsPerson = async ()=> {

  let randID = Math.floor(Math.random(10) * 10)

  let res = await axios.get(`https://swapi.dev/api/people/${randID}`)

  return res.data.name;
}

const App = () => {

  const [currentPerson, getStarWarsPerson] = useState()

  return (
    <div className="App">
      <h1>Hello</h1>

      <p>{`${currentPerson}`}</p>
    </div>
  );
}

export default App;
