import logo from './logo.svg';
import './App.css';

// import axios so it can be used
import axios from 'axios';

const getStarWarsPerson = async (id)=> {
  let res = await axios.get(`https://swapi.dev/api/people/${id}`)

  console.log("")
  return res.data.name;
}

const App = () => {

  const currentPerson = getStarWarsPerson(Math.floor(Math.random(10) * 10))

  return (
    <div className="App">
      <h1>Hello</h1>
      <p>{`${currentPerson}`}</p>
    </div>
  );
}

export default App;
