// use server requests to search for a specific user's stats with a search form


// import useState to work with variables and events
import { useState, useEffect } from 'react';
// import axios so it can be used
import axios from 'axios';

const FortniteStatTracker = () => {

    const [playerName, setPlayerName] = useState();

    const pathForKeys = '/config.json'
    let fortniteKey = 'none'
    axios.get(pathForKeys)
    .then((res) => console.log(res.data[0].key));
    
    async function submitHandler(e) {
        e.preventDefault();
       
        data.append("playerName", playerName)
        setPlayerName("")
        console.log(data)

        const newPlayer = await axios.post("/playerstats", data)
        .then((res) => {
            console.log(res)
            console.log(res.data)
            console.log(data)
            axios.get(`https://fortnite-api.com/v2/stats/br/v2/${res}`).then((res) => {
                console.log(res.data)
            })
        });
        
    }

    return (
        <div>
            <h1>Fortnite Stats Tracker BABYYYY!</h1>
            <h4>Find your stats</h4>
            <form onSubmit={submitHandler} method="post" id="stats-name">
                <label for="name">Fornite name:</label>
                <input type="text" id="name" placeholder="#1VicRoyBoy" value={playerName} />
                <small></small>

                <br></br>
                <button type="submit">Try!</button>
                <br></br>
            </form>
            <br></br><br></br>
        </div>
    );


}

export default FortniteStatTracker;
